from ._anvil_designer import Form1Template
from anvil import *
import anvil.server

class Form1(Form1Template):
  def __init__(self, **properties):
    self.init_components(**properties)

  def _submit_order(self, quantity):
    name = self.text_box_name.text
    address = self.text_box_address.text

    if not name or not address:
      self.label_status.text = "Please enter your Name and Address."
      self.label_status.visible = True
      return

    try:
      result = anvil.server.call('place_booking', name, address, quantity)
      self.label_status.text = result
      self.label_status.visible = True
      self.text_box_name.text = ""
      self.text_box_address.text = ""
    except Exception as e:
      self.label_status.text = f"Error: {str(e)}"
      self.label_status.visible = True

  def button_1_tank_click(self, **event_args):
    self._submit_order("1 Tank")

  def button_half_tank_click(self, **event_args):
    self._submit_order("Half Tank")

  def button_quarter_tank_click(self, **event_args):
    self._submit_order("Quarter Tank")
